class Items {
  Title _title;
  Desc _desc;
  String _code;
  String _codeOff;

  Title get title => _title;
  Desc get desc => _desc;
  String get code => _code;
  String get codeOff => _codeOff;

  Items({
      Title title, 
      Desc desc, 
      String code, 
      String codeOff}){
    _title = title;
    _desc = desc;
    _code = code;
    _codeOff = codeOff;
}

  Items.fromJson(dynamic json) {
    _title = json["title"] != null ? Title.fromJson(json["title"]) : null;
    _desc = json["desc"] != null ? Desc.fromJson(json["desc"]) : null;
    _code = json["code"];
    _codeOff = json["codeOff"];
  }

  Map<String, dynamic> toJson() {
    var map = <String, dynamic>{};
    if (_title != null) {
      map["title"] = _title.toJson();
    }
    if (_desc != null) {
      map["desc"] = _desc.toJson();
    }
    map["code"] = _code;
    map["codeOff"] = _codeOff;
    return map;
  }

}

